import React from "react";
import { FaStar } from "react-icons/fa";

const Card = (props) => {
  return (
    <div>
      <img
        //   src={`${process.env.PUBLIC_URL}/nkh_thai_passport.png`}
        src={props.img}
        alt={props.alt}
      />
      <span>
        <FaStar className="card--star" /> {props.rating}
      </span>
      <span>{props.title}</span>
      <span>
        <strong>{props.cost}</strong> / person
      </span>
    </div>
  );
};

export default Card;
